import React, { useState } from "react";
import axios from "axios";
import "./DietPlanForm.css";

const DietPlanForm = () => {
    const [formData, setFormData] = useState({
        age: "",
        gender: "",
        height: "",
        weight: "",
        activity_level: "",
        goal: "",
        specific_goal: "",
        medical_conditions: "",
        nutritional_needs: "",
        daily_diet: "",
        eating_out_frequency: "",
    });

    const handleChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                "http://127.0.0.1:8000/api/diet-plan/",
                formData
            );
            alert("Form submitted successfully!");
            console.log(response.data);
        } catch (error) {
            console.error("Error submitting form:", error);
            alert("Failed to submit the form.");
        }
    };

    return (
        <div className="diet-plan-form">
            <nav className="navbar">
                <h1>Diet Plan App</h1>
            </nav>
            <form className="form-container" onSubmit={handleSubmit}>
                <h1 className="form-heading">Personalized Diet Plan</h1>
                <h2 className="section-heading">Basic Information</h2>
                <div className="input-group">
                    <label>Age</label>
                    <input
                        type="number"
                        name="age"
                        value={formData.age}
                        onChange={handleChange}
                        placeholder="Enter your age"
                    />
                </div>
                {/* Repeat similar inputs for other fields */}
                <button type="submit" className="submit-button">
                    Submit
                </button>
            </form>
            <footer className="footer">
                <p>&copy; 2024 Diet Plan App. All rights reserved.</p>
            </footer>
        </div>
    );
};

export default DietPlanForm;
